(function(outputs, steps, stepResult, assertEqual) {
    // add test script here
	// 552c48888c033300964f4932b03eb092
	
	// var sys_id = "552c48888c033300964f4932b03eb092";
	
	var gr = new GlideRecord('incident');
	var gr_properties = new GlideRecord('sys_properties');
	
	//query priority 1 incidents in the state of either 'new' or 'active'.
	
	var num = 'INC0011024';
	var property_name = 'x_vmw_uemconn_0.test_incident_notes';
	
	gr.addQuery('number', num);
	gr.query();

	gr_properties.addQuery('name', property_name);
	gr_properties.query();
	
	var previous_note = ''
	while(gr_properties.next()) {
		previous_note = gr_properties.getValue('value');
		
		gs.info('demo previous note ' + previous_note);
	}
	
	gs.info('demo rows : ' + gr.getRowCount());
	
	var last_note = '';
	while(gr.next())
	{
		//print a list of the incident numbers updated
		gs.info('demo number : ' + gr.number);

		//add an entry to the 'work notes' journal field for each incident
		// gr.work_notes = "This is a high-priority incident. Please prioritize.";
		
		var notes = gr.work_notes.getJournalEntry(-1);
		
		gs.info('demo notes : ' + notes);
		var na = notes.split("\n\n");
		last_note = na[0];
		
		gs.info('demo journal : ' + last_note);
	}
	
	if (previous_note == last_note) {
		return false;
	} else {
		return true;
	}
	
