sys_variable_value.value:
(function execute(inputs, outputs) {

  // translate the message
  outputs.message = gs.getMessage(inputs.messagetotranslate);
  if (inputs.messagetotranslate == outputs.message) {
    	gs.info("Translation missing? : " + inputs.messagetotranslate + " = " + outputs.message);
  }

  // For testing
//  var str = gs.getMessage(inputs.messagetotranslate);
//  outputs.message = str.toUpperCase();

})(inputs, outputs);

