sys_hub_input_scripts.script:
{"save_response_as_attachment":{"scriptActive":false,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
"}}

sys_variable_value.value:
(function execute(inputs, outputs) {

  if (inputs.responseBody) {
     var returnedObject = JSON.parse(inputs.responseBody);
     outputs.ws1errorcode =  returnedObject.errorCode;
     outputs.ws1message =  returnedObject.message;
  } else {
     outputs.ws1errorcode =  "0";
  	 outputs.ws1message =  "Success";
  }

})(inputs, outputs);

