sys_rte_eb_script_operation.script:
(function(batch, output) {
	for (var i = 0; i < batch.length; i++) {
		var input = batch[i].input; // Value of the input column. 

		// Add your code here.

    var gr = new GlideRecord('cmdb_model');
    gr.addQuery('name', 'STARTSWITH', input);
    gr.query();
    gr.next();
    if (gr.sys_id){
       output[i] = gr.sys_id;
     } else {
       output[i] = "";
     }
		// Set each output element below
		//output[i] = output_variable_name;
	}
})(batch, output);
