sys_script_client.script:
function onChange() {
	var showsection = false;

	//add tab is WS1 UEM role is enabled and there is a caller and a configurationItem else hide it
	var isStandard = g_user.hasRole('x_646971_uemconn_0.WS1UEMStandard');
	if (isStandard){	
		var caller = g_form.getValue('caller_id');
		if (caller){
			var configItem = g_form.getValue('cmdb_ci');
			if (configItem){
				showsection = true;	
			}
		}
	}	

	//Show or hide the section using English name so will work when translated
//	var section = $$('span[tab_caption_raw="workspace_oneuem"]')[0].select('span[id*=section.]')[0];
	if (showsection == true) {
//		section.show();	
		g_form.setSectionDisplay('workspace_oneuem', true);	
	}
	else {
		g_form.setSectionDisplay('workspace_oneuem', false);	
//		section.hide();
	}
}
