sys_script_client.script:
function onLoad() {	
	//remove tab is WS1 UEM role is not enabled
	var isStandard = g_user.hasRole('x_646971_uemconn_0.WS1UEMStandard');
	if (!isStandard){	
		//g_form.setSectionDisplay('workspace_oneuem', false);

		//Hide the section using English name so will work when translated
		var section = $$('span[tab_caption_raw="workspace_oneuem"]')[0].select('span[id*=section.]')[0];
		section.hide();	
	}
	else {
		// Reset the message box
		g_form.setValue('x_646971_uemconn_0_ws1uem', "");

		// If the user has the standard WS1 UEM rights then check if advanced
		// and remove destructive actions from list if not
		var isAdvanced = g_user.hasRole('x_646971_uemconn_0.WS1UEMAdvanced');
		if (!isAdvanced){
			g_form.removeOption('x_646971_uemconn_0_ws1command', 'DeviceWipe');
			g_form.removeOption('x_646971_uemconn_0_ws1command', 'EnterpriseWipe');
		}
	}
}
