function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	if (newValue == 'true') {


//	var dialog = new GlideModal('x_646971_uemconn_0_WS1UEM popup'); 
	var dialog = new GlideDialogWindow('x_646971_uemconn_0_WS1UEM popup'); 
    dialog.setTitle('WS1 UEM Action details'); 
    dialog.setSize(550, 800); //Set the dialog size
    dialog.setPreference('si', g_form.getUniqueValue()); //pass current object id
    dialog.setPreference('value',newValue);//pass the selected value on the form	
    dialog.render(); //Open the dialog   
    }
}
