sys_script_include.script:
var ws1_get_incident_record = Class.create();
ws1_get_incident_record.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
   get_incident:function() {
		var gr_properties = new GlideRecord('x_646971_uemconn_0_ws1_testing');

		gr_properties.addQuery('name', 'test_incident_record');
		gr_properties.query();

		var test_value = '';
		if (gr_properties.next()) {
			test_value = gr_properties.getValue('value');
		}

		gs.info('Incident Id : ' + test_value);

		return test_value;

   } ,
   _privateFunction: function() { // this function is not client callable     
    }
 });
