sys_variable_value.value:
(function execute(inputs, outputs) {
  var devices = [];
  if (inputs.responseBody) {
    var returnedObject = JSON.parse(inputs.responseBody);
    var deviceArray = returnedObject.devices;
    if (deviceArray) {
      for (var i=0; i<deviceArray.length; i++) {
        var deviceObject = deviceArray[i];
        var device = {};
        device.uuid = deviceObject.Uuid;
        device.username = deviceObject.enrollment_user_name;
        device.useremailaddress = deviceObject.enrollment_user_email_address;
        device.devicereportedname = deviceObject.device_reported_name;
        device.locationgroupname =  deviceObject.organization_group_name;
        device.Udid = deviceObject.udid;
        device.platform = deviceObject.platform_name;
        device.id = deviceObject.Id.Value;
        device.serial_number = deviceObject.serial_number;
        device.imei = deviceObject.imei;
        device.model_identifier = deviceObject.model_identifier;
        device.model = deviceObject.model;
        device.operating_system = deviceObject.operating_system;
        device.compliance_status = deviceObject.compliance_status;
        device.ownership = deviceObject.ownership;
        device.enrollment_status = deviceObject.enrollment_status;
        devices.push(device);
      }
    }
  }
  outputs.devices = devices;
})(inputs, outputs);

