sys_script_client.script:
/**
WS1UEMAction onLoad script runs when the Incident page
opens, setting the default actions available to the user
and obtaining UEM configurations for further customizability.
**/
function onLoad() {
    var IN_PROGRESS_STATE = 2;
    var ga_version;
    var version;
    var currentstate;
    var sid;
    var ga;
    var configItem;
    var hasDeviceWipeRole;
    var hasEnterpriseWipeRole;
    var hasLockDeviceRole;
    var hasRemoteAssistRole;
    var hasSendMessageRole;
    var hasSyncDeviceRole;
    var hasDeviceLogsRole;
    var hasChangePasscodeRole;
    var hasSoftResetRole;
	var hasUEMUserRole;
    var showUEMTab = false;

    g_form.setValue("x_vmw_ws1uem_ws1action", "");

    ga_version = new GlideAjax("x_vmw_ws1uem.WS1GetPlatform");
    ga_version.addParam("sysparm_name", "get_platform");
    version = null;

    ga_version.getXML(function parse_response(response) {
        version = response.responseXML.documentElement.getAttribute('answer');

        hasUEMUserRole = g_user.hasRole("x_vmw_ws1uem.ws1uemuser");

        // Check if the WS1 UEM tab should be displayed based upon the user having a valid WS1 UEM role		
        if (!hasUEMUserRole || !version) {
            g_form.setSectionDisplay("workspace_oneuem", false);
        } else {
            currentstate = g_form.getValue("state");
            if (currentstate == IN_PROGRESS_STATE) {
                sid = g_form.getUniqueValue();
                ga = new GlideAjax("x_vmw_ws1uem.WS1GetActionFromOS");
                ga.addParam("sysparm_name", "get_action");
                ga.addParam("sysparm_sid", sid);

                ga.getXML(parse_os_name);

                configItem = g_form.getValue("cmdb_ci");
                if (!configItem) {
                    jslog("Removing user actions because of missing configuration item");
                }

                // Get the user's UEM roles
                hasDeviceWipeRole = g_user.hasRole("x_vmw_ws1uem.ws1uemdevicewipe");
                hasEnterpriseWipeRole = g_user.hasRole("x_vmw_ws1uem.ws1uementerprisewipe");
                hasLockDeviceRole = g_user.hasRole("x_vmw_ws1uem.ws1uemlockdevice");
                hasRemoteAssistRole = g_user.hasRole("x_vmw_ws1uem.ws1uemremoteassist");
                hasSendMessageRole = g_user.hasRole("x_vmw_ws1uem.ws1uemsendmessage");
                hasSyncDeviceRole = g_user.hasRole("x_vmw_ws1uem.ws1uemsyncdevice");
                hasDeviceLogsRole = g_user.hasRole("x_vmw_ws1uem.ws1uemdevicelogs");
                hasChangePasscodeRole = g_user.hasRole("x_vmw_ws1uem.ws1uemchangepasscode");
                hasSoftResetRole = g_user.hasRole("x_vmw_ws1uem.ws1uemsoftreset");

                // Remove any actions not assigned to a user or that don't have a configuration item
                if (!hasDeviceWipeRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "devicewipe");
                }
                if (!hasEnterpriseWipeRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "enterprisewipe");
                }
                if (!hasLockDeviceRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "lock");
                }
                if (!hasRemoteAssistRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "assist");
                }
                if (!hasSendMessageRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "message");
                }
                if (!hasSyncDeviceRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "syncdevice");
                }
                if (!hasDeviceLogsRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "devicelogs");
                }
                if (!hasChangePasscodeRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "changepasscode");
                }
                if (!hasSoftResetRole || !configItem) {
                    g_form.removeOption("x_vmw_ws1uem_ws1action", "softreset");
                }
            }
        }
    });

    /**
    Response callback from WS1GetActionFromOS to parse JSON configuration of UEM
    Actions based on the current device's OS and version. Value is updated onto
    the incident page via a field that can be read by the dialog popup when it
    renders.
    **/
    function parse_os_name(response) {
        var key;
        response = response.responseXML.documentElement.getAttribute("answer");
        jslog("Queried response for Actions for OS = " + response);

        $("incident.x_vmw_ws1uem_ws1_actions_response").value = response;

        response = JSON.parse(response);
        for (key in response) {
            if (response[key] == false && key != "") {
                g_form.removeOption("x_vmw_ws1uem_ws1action", key);
            }
        }
        $("incident.x_vmw_ws1uem_ws1action").disabled = false;

    }
}
