sys_script_include.script:
function get_http_auth(auth_name) {
	var http_table = new GlideRecordSecure('http_connection');
	var hostid;
	var active;
	http_table.addQuery('name', auth_name);

	http_table.query();
	if (http_table.next()) {
		hostid = http_table.host;
		active = http_table.active;
	} else {
		hostid = "";
		active = "";
	}

	var response = {"hostid" : hostid.toString(), "active" : active.toString()};
	return response;
}

function get_oauth_entity(auth_name) {
	var oauth_table = new GlideRecordSecure('oauth_entity');
	oauth_table.addQuery('name', auth_name);
	oauth_table.query();
	if (oauth_table.next()) {
		var clientid = oauth_table.client_id;
		//		don't retrieve the existing client secret but just set it to a temp value;
		var clientsecret = "********************";
		var tokenurl = oauth_table.token_url;
	}

	var response = {"clientid" : clientid.toString(), "clientsecret" : clientsecret, "tokenurl" : tokenurl.toString()};
	return response;

}

function get_basic_auth_credentials(auth_name) {
	var basic_table = new GlideRecordSecure('basic_auth_credentials');
        basic_table.addQuery('name', "Workspace ONE UEM ITSM Basic Credentials");
        basic_table.query();
        if (basic_table.next()) {
            var user_name = basic_table.user_name;
            var user_secret = "********************";
		}

		var response = {"user_name" : user_name.toString(), "user_secret" : user_secret};
		return response;
}

function get_tenant_code() {
	var action_table = new GlideRecordSecure('x_vmw_ws1uem_ws1uemappconfig');
    action_table.addQuery('name', "WS1UEMTenantCode");
	action_table.query();
	if (action_table.next()) {
		var tenant_code = action_table.value;
	}

	var response = {"tenant_code" : tenant_code.toString()};
	return response;
}

var WS1GetAuthCredentials = Class.create();
WS1GetAuthCredentials.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    get_credentials:function() {
		var oauth_response = get_http_auth("Workspace ONE UEM ITSM OAuth Credentials");
		var basic_response = get_http_auth("Workspace ONE UEM ITSM Basic Credentials");

		var oauth_entity_response = get_oauth_entity("Workspace ONE UEM ITSM OAuth Provider");
		var basic_auth_credentials = get_basic_auth_credentials("Workspace ONE UEM ITSM Basic Credentials");

		var tenant_code = get_tenant_code();

		var full_response = {"oauth" : oauth_response, "basic" : basic_response, "oauth_entity" : oauth_entity_response, "basic_auth_credentials" : basic_auth_credentials, "tenant_code" : tenant_code};

		return JSON.stringify(full_response);
	},
	get_credentials_2_0:function() {
		var oauth_initiator_url = '';
		var oauth_table = new GlideRecordSecure('oauth_2_0_credentials');
		oauth_table.addQuery('name', "Workspace ONE UEM ITSM OAuth Credentials");
		oauth_table.query();
		if (oauth_table.next()) {
			var oauthProfileId = oauth_table.oauth_entity_profile;
			var oauthRequestorSysId = oauth_table.sys_id;
			var oauthRequestorContext = 'oauth_2_0_credentials';

			oauth_initiator_url = '/oauth_client_credentials_input.do' +
				'?sysparm_oauth_requestor_context=' + oauthRequestorContext +
				'&sysparm_oauth_requestor=' + oauthRequestorSysId +
				'&sysparm_oauth_provider_profile=' + oauthProfileId;

			var full_response = {"oauthProfileId" : oauthProfileId.toString(), "oauthRequestorSysId" : oauthRequestorSysId.toString(), "oauthRequestorContext" : oauthRequestorContext.toString(), "oauth_initiator_url" : oauth_initiator_url};

			return JSON.stringify(full_response);
		}
	},
    type: 'WS1GetAuthCredentials'
});
