sys_script_include.script:
/**
Query for obtaining a testable incident record
from the WS1 Testing table. These fields are used for
unit tests in which the popup can't access parent records.
**/
var WS1GetIncidentRecord = Class.create();
WS1GetIncidentRecord.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    get_incident:function() {
        var test_value;
        var gr_properties = new GlideRecordSecure("x_vmw_ws1uem_ws1uemappconfig");

        gr_properties.addQuery("name", "WS1UEMTestIncidentRecord");
        gr_properties.query();

        test_value = "";
        if (gr_properties.next()) {
            test_value = gr_properties.getValue("value");
    }

    return test_value;

    } ,
    _privateFunction: function() { // this function is not client callable
    }
});
