sys_script_include.script:
var WS1UpdateUEMConsoleURL = Class.create();
WS1UpdateUEMConsoleURL.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	update_console_url:function() {
		gs.info("> demo getting uem url");

        var uem_url;
		var http_table = new GlideRecordSecure('http_connection');			
			http_table.addQuery('name', "Workspace ONE UEM ITSM OAuth Credentials");
            http_table.query();
            if (http_table.next()) {
                if (http_table.active) {
                    uem_url = http_table.protocol + "://" + http_table.host;
                } else {
                    http_table = new GlideRecordSecure('http_connection');
                    http_table.addQuery('name', "Workspace ONE UEM ITSM Basic Credentials");
                    http_table.query();
                    if (http_table.next()) {
                        if (http_table.active) {
							uem_url = http_table.protocol + "://" + http_table.host;
                        }
                    }             
				}
            }

		var gr_properties = new GlideRecordSecure("x_vmw_ws1uem_ws1uemappconfig");

		gr_properties.addQuery("name", "WS1UEMConsoleURL");
		gr_properties.query();
		if (gr_properties.next()) {
			gr_properties.value = uem_url;
		}

		gr_properties.update();

		return "true";
	},
    type: 'WS1UpdateUEMConsoleURL'
});
