sys_ui_page.client_script:
onLoad();

function onLoad() {

	var action_table = new GlideRecord('sys_choice');
	action_table.addQuery('element', "x_vmw_ws1uem_ws1action");
	action_table.query();

	while (action_table.next()) {

		if (action_table.value == 'devicelogs') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_requestlogs").checked = false;
			}
			else {
				$("input_ws1_requestlogs").checked = true;
			}
		}
		else if (action_table.value == 'enterprisewipe') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_enterprisewipe").checked = false;
			}
			else {
				$("input_ws1_enterprisewipe").checked = true;
			}
		}
		else if (action_table.value == 'devicewipe') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_devicewipe").checked = false;
			}
			else {
				$("input_ws1_devicewipe").checked = true;
			}
		}
		else if (action_table.value == 'syncdevice') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_syncdevice").checked = false;
			}
			else {
				$("input_ws1_syncdevice").checked = true;
			}
		}
		else if (action_table.value == 'assist') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_remoteassist").checked = false;
			}
			else {
				$("input_ws1_remoteassist").checked = true;
			}
		}
		else if (action_table.value == 'message') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_message").checked = false;
			}
			else {
				$("input_ws1_message").checked = true;
			}
		}
		else if (action_table.value == 'softreset') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_softreset").checked = false;
			}
			else {
				$("input_ws1_softreset").checked = true;
			}
		}
		else if (action_table.value == 'changepasscode') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_changepasscode").checked = false;
			}
			else {
				$("input_ws1_changepasscode").checked = true;
			}
		}
		else if (action_table.value == 'lock') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_lock").checked = false;
			}
			else {
				$("input_ws1_lock").checked = true;
			}
		}
		else if (action_table.value == 'refreshdevices') {	
			if (action_table.inactive == 'true') {
				$("input_ws1_refreshdevices").checked = false;
			}
			else {
				$("input_ws1_refreshdevices").checked = true;
			}
		}
	}
}


function setValues() {	
	var action_table = new GlideRecord('sys_choice');
	action_table.addQuery('element', "x_646971_uemconn_0_ws1command");
	action_table.query();
	while (action_table.next()) {


		if (action_table.value == 'devicelogs') {	
			if ( $("input_ws1_requestlogs").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		if (action_table.value == 'enterprisewipe') {	
			if ( $("input_ws1_enterprisewipe").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		if (action_table.value == 'devicewipe') {	
			if ( $("input_ws1_devicewipe").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		if (action_table.value == 'syncdevice') {	
			if ( $("input_ws1_syncdevice").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		if (action_table.value == 'assist') {	
			if ( $("input_ws1_remoteassist").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		if (action_table.value == 'message') {	
			if ( $("input_ws1_message").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		if (action_table.value == 'softreset') {	
			if ( $("input_ws1_softreset").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		if (action_table.value == 'changepasscode') {	
			if ( $("input_ws1_changepasscode").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}	

		if (action_table.value == 'lock') {	
			if ( $("input_ws1_lock").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		if (action_table.value == 'refreshdevices') {	
			if ( $("input_ws1_refreshdevices").checked == false) {
				action_table.inactive = true;
			}
			else {
				action_table.inactive = false;
			}
		}

		action_table.update();
	}	
} 

// update checked box
function updateValue(cb, actionType) {
	var action_table = new GlideRecord('sys_choice');
	action_table.addQuery('element', "x_vmw_ws1uem_ws1action");
	action_table.query();
	while (action_table.next()) {
		if (action_table.value == actionType) {	
			action_table.inactive = !cb.checked;
			action_table.update();
			return;
		}
	}
}
