sys_ui_page.client_script:
onLoad();

function onLoad() {	
	var stringText = getMessage('WS1UEMCMDBIntegration');
	$("div_ws1_cmdb_text").innerHTML = getMessage(stringText);

	// check if the CMDB connector is installed
	var ts = new GlideRecord("sys_store_app"); 	
	stringText = getMessage('WS1UEMCMDBConnector');
	ts.addQuery("name", stringText);
	ts.query();
	ts.next();

	//get the integration system parameter 
	var action_table = new GlideRecord('sys_properties');
	action_table.addQuery('name', "x_vmw_ws1uem.WS1UEMCMDBIntegration");	
	action_table.query();
	action_table.next();

	// if installed get the state of our system parameter
	if (ts.name) {
		$("input_ws1_cmdb_integration").disabled = false;

		if (action_table.value == "true") {
			$("input_ws1_cmdb_integration").checked = true;
		}
		else {
			$("input_ws1_cmdb_integration").checked = false;
		}
	}
	else {
		action_table.value = "false";
		$("input_ws1_cmdb_integration").disabled = true;
	}
}


// update system paramter when the checkbox being changed
function updateValue(cb) {
	var action_table = new GlideRecord('sys_properties');
	action_table.addQuery('name', "x_vmw_ws1uem.WS1UEMCMDBIntegration");
	action_table.query();
	action_table.next();

	if (cb.checked) {
		action_table.value = "true";
	}
	else {
		action_table.value = "false";
	}

	action_table.update();
}
